'use strict';

function _interopDefault (ex) { return (ex && (typeof ex === 'object') && 'default' in ex) ? ex['default'] : ex; }

var tap = _interopDefault(require('tap'));
var util = require('./util.js');
var nodeOsc = require('node-osc');

tap.beforeEach(util.beforeEach);

function flaky() {
  return process.release.lts === 'Dubnium' && process.platform === 'win32';
}

function skip(t) {
  t.skip(`flaky ~ ${t.name}`);
  t.done();
}

util.test('osc: argument message no callback', (t) => {
  if (flaky()) return skip(t);

  const oscServer = new nodeOsc.Server(t.context.port, '0.0.0.0');
  const client = new nodeOsc.Client('0.0.0.0', t.context.port);

  t.plan(1);

  oscServer.on('message', (msg) => {
    oscServer.close();
    client.close();
    t.deepEqual(msg, ['/test', 1, 2, 'testing'], 'We should receive expected payload');
  });

  client.send('/test', 1, 2, 'testing');
});

util.test('osc: client with callback and message as arguments', (t) => {
  if (flaky()) return skip(t);

  const oscServer = new nodeOsc.Server(t.context.port, '0.0.0.0');
  const client = new nodeOsc.Client('0.0.0.0', t.context.port);

  t.plan(2);

  oscServer.on('message', (msg) => {
    oscServer.close();
    t.deepEqual(msg, ['/test', 1, 2, 'testing'], 'We should receive expected payload');
  });

  client.send('/test', 1, 2, 'testing', (err) => {
    t.error(err, 'there should be no error');
    client.close();
  });
});
